let activeRow=msg.payload;
let activeFieldName=msg.topic;
let refreshMsg=msg;
    //restituisce l'indice della riga con rowId==rowId in table
function posOf(rowId,table){
        //probable shortcut
    if(table[rowId].rowId==rowId)
        return rowId;
    let i=0
    for(let row of table){
        if(row.rowId==rowId)
            return i;
        i++;
    }
}



    //create a NEW table without the row that needs to be deleted //preserves table[rowId].rowId == rowId
    //then refresh frontend
if (activeFieldName == "deleteButton") {
    let table = flow.get("queueTable");
    let updatedTable = new Array();
    for (let row of table) {
        if (row.rowId != msg.payload.rowId)
            updatedTable.push(row);
    }

    flow.set("queueTable", updatedTable);
    node.warn("deleted");
        //refresh frontend
    return refreshMsg;
}

else if (activeFieldName.test(/sendToPressa/)) {
    let table = flow.get("queueTable");
    let tabUtils = flow.get("tabUtils");

    let state = activeRow[activeFieldName + "State"] //ex. sendToPressa1State // 0 "clickToSend" | 1 "loaded" | -1 "error"
    //state = !state; //loaded/error -> clickToSend   |   clickToSend -> loaded


    if(state){
            //reset state
        tabUtils.queue.sendToPressButton.setState("clickToSend", activeFieldName, table[posOf(activeRow.rowId)]); //changes displayed icon/button //takes state, pressNumber, rowReference_to_edit
            //reset plc data and refresh frontend
        flow.set("queueTable", table);
        node.warn("canceled send to Pressa" + activeFieldName.match(/sendToPressa([0-9]+/)[1]);
        return [refreshMsg,{ topic: msg.topic, action:"reset", payload: "" }]
    }
    else{
            //reset all other buttons in the same column
        for(let row of table)
            if(row[activeFieldName+"State"] === 1)
                tabUtils.queue.sendToPressButton.setState("clickToSend", activeFieldName, table[posOf(activeRow.rowId)]);
            //set active cell to "sent to plc" icon
        tabUtils.queue.sendToPressButton.setState("loaded", activeFieldName, table[posOf(activeRow.rowId)]);
            //write to plc and refresh frontend
        flow.set("queueTable", table);
        node.warn("sent to Pressa" + activeFieldName.match(/sendToPressa([0-9]+/)[1]);
        return [refreshMsg , { topic: msg.topic, action:"sendTo",payload: activeRow }];
    }

}
    //updates flow.table without refreshing frontend
else {
    flow.set("queueTable",msg.options.data)
    return null;
}

