const tabUtils={
    /*queueDict:{
        sendToPressButton:{
            "clickToSend":"<center><span ><i class='fa fa-arrow-up' style=\"padding-right:5px\"></i>load</span></center>",
            "loaded":"<center><span ><i class='fa fa-check' style=\"color:green;\"></i></span></center>",
            "fail": "<center><span ><i class='fa fa-times' style=\"color:red;\"></i></span></center>"
        }
    },*/
    queue:{
        sendToPressButton:{
            states: {
                "clickToSend":"<center><span ><i class='fa fa-arrow-up' style=\"padding-right:5px\"></i>load</span></center>",
                "loaded":"<center><span ><i class='fa fa-check' style=\"color:green;\"></i></span></center>",
                "fail": "<center><span ><i class='fa fa-times' style=\"color:red;\"></i></span></center>"
                //0 (click2Send) | 1 (sentAndOk) | -1 (fail?)
            },



            /** 
             * @param {(0|"clickToSend"|1|"loaded"|-1|"fail")} state to set
             * @param {string} fieldName name of the button to affect, like "sendToPressa1"
             * @param {object} reference to the row to edit, must have a fieldName button.
            */
            setState(state,fieldName,rowRef){
                var stateString;
                if(typeof(state) == "string"){
                    stateString=state;
                    switch (stateString) {
                        case "clickToSend": { state = 0; break; }
                        case "loaded": { state = 1; break; }
                        case "fail": { state = -1; break; }
                        default: { node.error("unknown button state " + state + " , set to clickToSend") }
                    }
                }
                else{
                    switch(state){
                        case 0: { stateString="clickToSend"; break; }
                        case 1: { stateString="loaded"; break; }
                        case -1: { stateString="fail"; break; }
                        default: { node.error("unknown button state "+state+" , set to clickToSend")}
                    }
                }
                rowRef[fieldName]=this.states[stateString];
                rowRef[fieldName+"State"]=state;
                return;
            }
        },
        newRow(rowId) {
            var row = {
                rowId: rowId,
                deleteButton: "<center><i class='fa fa-trash-o' ></i></center>",
                id: "",
                quantToDo: 10,
                quantProd: 0,
                progress: this.quantProd / this.quantToDo,
                //sendToPressa1,2,3.. :
                //sendToPressa1(2,3..)State: 0 (click2Send) | 1 (sentAndOk) | -1 (loading?)
            }

            let presse = ["Pressa1", "Pressa2", "Pressa3", "Pressa4"];
            for (let p of presse) {
                row["sendTo" + p] = `<center>
                <span style="display:inline; "><i class='fa fa-arrow-up' style="padding-right:5px;"></i>load</span>
                <span style="display:none; "><i class='fa fa-check' style="color:green;"></i></span>
                <span style="display:none; "><i class='fa fa-cross' style="color:red; padding-right:5px; "></i> errore</span>
            </center>`; //onCLick works
            }
            return row;
        }
    },
    newRowId(table){
            //load all possible indexes
        var indexes=new Array()
        let i=0;
        for(let row of table)
            indexes[i++]=true;
        indexes[i++] = true;
            //flag off the used ones
        for(let row of table)
            indexes[row.rowId]=false;
            //return the first available one
        i=0;
        while(!indexes[i])
            i++;
        return i;
    }
}


