const table=msg.payload;
const last = context.get("last") ?? {};

function passThrough(){
    context.set("last", table);
    node.send(msg);
}

for(let row of table){
    let keys=Object.keys(row);
    if(!keys)
        if(!Object.keys(last)){
            passThrough();
            node.warn("bothEmpty");
            return;
        }

    if(table.length != last.length){
        passThrough();
        node.warn("different lengths");
        return;
    }
    let i=0;
    for(let k of keys)
        if(!last[i].hasOwnProperty(k) || row[k]!==last[i][k]){
            node.warn({l:last,n:row,k:k,i:i});
            passThrough();
            return
        }
}

