flow.getDefaultQuery = function (address = 0, quantity = 2) {
    return {
        'payload':{
            'value': null,
            'fc': 3, //mode: Holding Register
            //'unitid': global.inverterUnitId //overrides default
            'address': address,
            'quantity': quantity
        }
    };
}

//from,to   string|int  hex string like "ox802" or int like 2050
flow.queryRegistry= function (from, to,sequenceName="") {
    if (typeof (from) == "string")
        from = parseInt(from);
    if (typeof (to) == "string")
        to = parseInt(to);
    let ret = flow.getDefaultQuery(from, to - from + 1);
    ret.name=sequenceName;
    return ret;
}

