//context.maxErrCount=0;
context.timeout=1600;
//context.maxTimeout=0;
context.isDisplaying=false;



context.displayError = function (errName, duration /*, startTime=0*/) {
    context.isDisplaying=true;
    let startTime=0;
    node.warn(errName); //per qualche motivo tutti i parametri della funzione restano undefined

    node.status({ fill: "red", shape: "dot", text: errName });
    setTimeout(() => {
        node.status({ fill: "red", shape: "ring", text: errName });
    }, duration/4 + startTime);
    setTimeout(() => {
        node.status({ fill: "red", shape: "dot", text: errName });
    }, duration*3/4 + startTime);
    setTimeout(() => {
        node.status({ fill: "gray", shape: "ring", text: "" });       
        context.nextError();
    }, duration + startTime);
}

context.addErr = function (errName) {
    if (context.isDisplaying){
        context.errStack.push(errName);
        node.log(context.errStack);
    }
    else
        context.displayError("someText", context.timeout);
}
context.nextError = function () {
    if (context.errHead == context.errStack.length) {
        context.isDisplaying=false;
        node.status({ fill: "gray", shape: "ring", text: "" });
        context.errStack = new Array()
        context.errHead = 0;
        return
    }
    else {
        context.displayError(context.errStack[context.errHead],context.timeout);
        context.errHead++;
    }
}

