    "sortByType": function sortByType(source, ...types) {

        if (!Array.isArray(source))
            if (typeof (source) == "object") { //assume it's an argument object
                try {
                    let tmp = [];
                    for (var arg of source)
                        tmp.push(arg);
                    source = tmp;
                } catch {
                    return node.error({ txt: "global." + thisClassName + ".sortByType(): source is not an array", source: source, args: arguments });
                }
            }
            else
                return node.error({ txt: "global." + thisClassName + ".sortByType(): source is not an array", source: source, args: arguments });
        if (source.length !== types?.length)
            return node.error({ txt: "global." + thisClassName + ".sortByType(): source lenght doesn't match types[]'s", source: source, types: types, args: arguments });

        const typedPairs = source.map(item => new Object({ val: item, type: typeof (item) }));
        let findType = (type) => typedPairs.find(pair => pair.type == type) ?? node.warn({ txt: "missing param typed " + type });

        var errors;
        var ret = [];
        for (var type of types) {
            let idx = typedPairs.findIndex(pair => pair.type == type);
            if (idx < 0) {
                errors ?? (errors = []);
                errors.push(type);
                ret.push(null);
            }
            else {
                ret.push(typedPairs[idx]);
            }
        }
        if (errors)
            node.error({ txt: `global.${thisClassName}.${sortByType.name}(): missing param typed ${errors.length > 1 ? `[${errors}]` : errors[0]}`, args: arguments });
        return ret;
    },
