    /*v da testare v* /
    /**
     * Stampa un messaggio, con strategie diverse a seconda del tipo e del debugLevel globale.  
     * Tutti i log dovrebbero passare di qui.  
     * Gli argomenti vengono riordinati automaticamente. dbgPrint({extra:5},"messaggio"), dbgPrint("verbose", "something happened") sono tutte chiamate legali
     * 
     * @param _txt testo messaggio
     * @param _infoObj campi extra da allegare, oltre a quelli automatici. In caso di clash vince infoObj.
     * @param _level tipologia messaggio. accetta sia interi che stringhe. gli interi mappano utils.dbgLevels * /
    "dbgPrint": function dbgPrint(_txt, _infoObj, _level) {
        const utils = global.get(thisClassName);
        const dbgLevelsMap = utils.dbgLevelsMap;

        //compile-like check: all expected level ids exist
        utils.assert(["warning", "error", "debug", "verbose"].every(key => (utils?.dbgLevelsMap?.[key] ?? null) !== null), "dpgPrint(): missing one or more expected dbg level", { exp: ["warning", "error", "debug", "verbose"], got: dbgLevelsMap });

        node.warn({txt:"args pre sort",args:{txt:_txt,level:_level,infoObj:_infoObj}})

        //sort arguments, filling them with def vals when necessary
        let argArray = [_level, _txt, _infoObj];
        var typedPairs = argArray.map(item => new Object({ val: item, type: typeof (item) }));

        //pops a value from typedPairs, or returns defVal if nothing matches
        let popValOfType = (type, defVal) => {
            var idx = typedPairs.findIndex(pair => pair.type == type);
            if (idx >= 0) {
                var val = typedPairs[idx].val;
                utils.removeIdx(typedPairs, idx);
                return val;
            }
            else
                return defVal;
        }
        var txt = popValOfType("string","noTxt");
        var level = popValOfType("number", null) ?? popValOfType("string", null) ?? dbgLevelsMap["debug"];
        var infoObj = popValOfType("object", {});

        if(typeof level == "string"){
            //sort txt and type, as they are both strings
            let validLiterals = ["warning", "warn", "error", "err", "debug", "dbg", "console", "stdout"];
            if (txt.length <= 7 && level.length > 7
                || validLiterals.includes(txt))
                [level, txt] = [txt, level];
            //convert to numeric id
            switch (level) {
                case "warning":
                case "warn":
                    level = dbgLevelsMap["warning"];
                    break;
                case "error":
                case "err":
                    level = dbgLevelsMap["error"];
                    break;
                case "debug":
                case "dbg":
                    level = dbgLevelsMap["debug"];
                    break;
                case "verbose":
                case "verb":
                    level = dbgLevelsMap["verbose"];
                    break;
                default:
                    node.error("error", "unknown log level: " + level);//, { valid: validLiterals, got: level, args: arguments });
                    break;
            }
        }

        node.warn({ txt: "args post sort", args: { txt: txt, level: level, infoObj: infoObj } });

        //define log vars
        if (true || level <= global.get("dbgLevel")){
            //https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Function/caller
            var fName;
            try { fName = dbgPrint.caller; } catch { fName = "unreadable" }
            var nodeJsStack;
            try { nodeJsStack = new Error().stack } catch { nodeJsStack = "unreadable" }
            //default logging strategy
            let defEditorLog = (logger) => logger.call(this,
                utils.mergeRec({
                    txt: txt,
                    n: this.node.name,
                    auto: {
                        f: fName,
                        id: this.node.id,
                        _this: this,
                        nodeJsStack: nodeJsStack
                    }
                }, infoObj));
            node.warn({ txt: "context", ctx: [Object.getOwnPropertyNames(dbgPrint.caller).map(name => name + ":" + dbgPrint.caller[name]), Object.getOwnPropertyNames(arguments.callee).map(name => name + ":" + arguments.callee[name])]});
            //pick logging strategy
            switch (level) {
                case dbgLevelsMap["warning"]:
                    defEditorLog(this.warn);
                    break;
                case dbgLevelsMap["error"]:
                    defEditorLog(this.warn);
                    break;
                case dbgLevelsMap["debug"]:
                    defEditorLog(this.warn);
                    break;
                case dbgLevelsMap["verbose"]:
                    defEditorLog(this.warn);
                    break;
                default:
                    node.error("unknown log level " + level);
                    break;
            }
        }

    },

