                            break;
                        }
                        case "clearing":{
                            this.setStateInRow(state, fieldName, rowRef);
                            break;
                        }
                    }
                    return;
                },
                */


                /**
                 * true if referenced press is currently loaded into the plc
                 * @param {string} fieldName name of the button to affect, like "sendToPressa1"
                 * @param {object} reference to the row to check, must have a fieldName button.
                */
                isLoaded(fieldName, rowRef) { //true se questa riga  attualmente caricata nel plc
                    let stateString = this.getStateString(rowRef[fieldName + "State"]);
                    return (stateString == "clearing" || stateString == "loaded");
                }
            },

            newRow(rowId) {
                var row = {
