/** @summary repeatedly tries to fetch all varNames with store.get().
     *  @desc may be used with inverse parameters. Like (?...varNames, ?opts, cb). Supports ("name","name",cb), (opts, cb) cases.
        * @param {function} cb called once ALL globalName are defined
        * @param {Object} opts object holding optional function params. can be omitted passing the first globalName instead.
        * @param {number} opts.timeout ms delay after which stop trying and either call opts.errorCb() or log an error. defaults to 15000
        * @param {number} opts.retryDelay ms between each attempt. defaults to 100
        * @param {function} opts.errorCb function to call after timeout. defaults to falsy, logging a warning
        * @param  {...any} varNames series of strings
        */
function callWhenDef(cb, opts, ...varNames) {
    //check for argument reverse order -> //treat as (?...varNames, ?opts, cb) //easier to read
    if (typeof (cb) == "string") // @ts-ignore //ignore type mismatch
        var [cb, opts = {}, ...varNames] = [...arguments].reverse(); //"var",cb -> cb, "var"
    //check for opts presence
    if (typeof (opts) == "string")
        varNames.push(opts);
    let { timeout = 3000, retryDelay = 100, errorCb = null, ctx = "global" } = (typeof (opts) == "string" ? {} : (opts ?? {}));
    var tout, intv, tmp;
    //switch context to the one specified in ctx
    var store = ctx === "global" ? global : ctx === "flow" ? flow : ctx === "context" ? context : (() => { node.error("[callWhenDef] param opts.context must be a string among ['context','global','flow']. passed " + ctx + " of type " + typeof (ctx)); return global })();
    //const store = function (c = ctx) { return {global:global, flow:flow, context:context}[c] ??  (()=>{ node.error("[callWhenDef] param opts.context must be a string among ['context','global','flow']"); return global })() }
    //detect and warn about "return msg" erroneous usage //(cb is not supposed to return anything)
    let wrappedCb = () => tmp = cb() && (tmp ?? node.warn({ txt: "[callWhenDef] detected possible typo, did you mean to use node.send(msg)?", returned: tmp, args: arguments }))

    //try immediately, before doing any work
    if (varNames.every(name => store.get(name) !== undefined))
        return wrappedCb()
    //set timeout unless false or not specified
    if (timeout) {
        tout = setTimeout(() => {
            clearInterval(intv);
            errorCb ? errorCb(...arguments) : node.warn({ err: "callWhenDef_Timeout", arg: arguments, isDef: varNames.map(name => store.get(name) !== undefined) })
        }, timeout);
    }
    //set store-checking interval
    intv = setInterval(() => {
        if (!varNames.every(name => store.get(name) !== undefined))
            return
        clearTimeout(tout);
        clearInterval(intv);
        wrappedCb();
    }, retryDelay)
}

