    //case insensitive
function getId(name,type=null,source=json){
    name=name.toLowerCase();
    for(let item of source)
        if(item.name && item.name.toLowerCase()==name && (!type || type==type))
            return item.id;
    for(let item of fallback)
        if (item.name && item.name.toLowerCase()==name && (!type || type==type))
            return item.id;
    node.error("id di "+name+( !type ? "" : " di tipo "+type)+" non trovato");
    node.warn({name,type,source,fallback});
    return "";
}
function getGroupId(name, source=json) {
    return getId(name, "ui_group", source);
}
function getTabId(name, source=json) {
    return getId(name, "ui_tab", source);
}



