    //; at each line's end */
class List {
    head;
    //#lastPrev;
    //get last(){ return (this.#lastPrev ? this.#lastPrev.next : this.head) }
    last;

    constructor() {
        this.head = null;
        this.last = this.head;
    }

    static isListNode(val){
        return (isObject(val) && Object.hasOwnProperty("next"))
    }
    static isTwoWayListNode(val){
        return (List.isListNode(val) && Object.hasOwnProperty("prev"));
    }

    peekFirst() {
        return (this.head ? this.head.value : null);
    }

    popFirst() {
        let res = this.peekFirst();
        //remove
        if (this.head && this.head.next)
            this.head = this.head.next;
        return res;
    }

    peekLast() {
        return (this.last ? this.last.value : null);
    }

    popLast() {
        if (!this.head)
            return null;

        let res = this.peekLast();

        let n = this.head,
            n2last = null; //'next-to-last' <=> 'penultimate'
        while (n.next) {
            n2last = n;
            n = n.next;
        }
        n2last.next = null;
        this.last = n2last;

        return res;
    }

    /** creates a one-way list node with value=val */
    static newNode(val) {
        return { value: val, next: null }
    }
    /** creates a two-way list node with value=val and empty prev */
    static newTwoWayNode(val) {
        return { value: val, prev: null, next: null }
    }
    /** creates a two-way list node with value=val and empty prev */
    static _append(el, listHead){
        if (listHead) {
            let node = listHead;
            while (node.next) {
                node = node.next;
            }
            node.next = (List.isListNode(el) ? el : List.newNode(el))
            return node.next;
        }
        else {
            return (List.isListNode(el) ? el : List.newNode(el));
        }
    }
    append(el) { List._append(el, this.head) }

    static _appendTwoWay(el, listHead) {
        if (List.isTwoWayListNode(el))
            List._append(el, listHead)
        else
            List._append(List.newTwoWayNode(el), listHead);
    }


    static _prepend(el, listHead){
        let newHead  = (List.isListNode(el) ? el : List.newNode(el))
        newHead.next = listHead;
        return newHead;
    }
    prepend(el) {
        this.head = List._prepend(el, this.head)
    }

    static _prependTwoWay(el,listHead){
        let newHead = List._prepend(el, listHead);
        if(listHead)
            listHead.prev = newHead;
        return newHead;
    }

    static iterator(listHead) {
        return {
            last: listHead,
            next() {
                if (this.last === null)
                    return { done: true }
                else {
                    let ret = { value: this.last.value, done: false }
                    this.last = this.last.next;
                }
            },
            return(){} //optional, do cleanup routine (close db connection and such)
        }
    }

    *[Symbol.iterator](){
        return List.iterator(this.head);
    }

    toArray(){
        var ret = [];
        for(let val of this.head)
            ret.push(val)
        return ret;
    }

    toString(){
        return this.toArray.toString();
    }

    values(){
        return this.toArray();
    }

}

