const utils=global.get("utils");

function isLost(status) {
    if (global.get("utils").isNotSet("status")) throw new Error("[isLost()] status can't be null");
    return status.fill == "red";
}

function isWarn(status){
    if (global.get("utils").isNotSet("status")) throw new Error("[isWarn()] status can't be null");
    return status.fill == "orange";
}

function isOk(status) {
    if (global.get("utils").isNotSet("status")) throw new Error("[isOk()] status can't be null");
    return status.fill == "green"; //!isWarn(status) && !isLost(status);
}

if(!(msg?.status?.fill ?? false)){
    return node.warn({txt:"missing status or fill",full:msg,stat:msg.status,fill:msg.status.fill});
}
else{
    //update context
    if(msg.origin=="prodExc")
        context.set("prodExc", msg.status)
    else
        context.set("status", msg.status)

    var status = context.get("status") ?? {fill:"red"};
    //var prodExc= context.get("prodExc") ?? {fill:"red"};
    var prodExc = status;

    if (isLost(status) || isLost(prodExc)) {
        msg.payload=false;
        return msg;
    }
    else if (isLost(status) || isLost(prodExc)) {
        msg.payload = "warn";
        return msg;
    }
    else if(isOk(status) && isOk(prodExc)){
        msg.payload = true;
        return msg;
    }
    else
        return //do nothing
}

