//enqueue msg after increasing delay
    //calc delay
let a = msg.attempts - 1;
let base = 4000; //4, 6, 9, 13 //4, 6.4
var delay = (Math.pow(3, a) / Math.pow(2, a)) * base // ( 3^i/2^i )*d <=> di+1 = di + (1/2)di
delay = Math.min(delay, 3600000) //max 60m
    //set timeout
let tout = setTimeout(() => {
    //node.warn({ txt: "sent after", d: delay })
    node.send(msg, false);
}, delay)

//track timeouts
    //at max size replace the older one
var allTouts = context.get("timeoutsRefArray") ?? [];
let newItem = { tout: tout, full: msg };
if (allTouts.length > 5000) {
    //find older
    let maxIdx;
    for (let i = 0; i < allTouts.length; i++)
        if (allTouts[i].a > allTouts[maxIdx].a)
            maxIdx = i;
