node.status({ fill: "yellow", text: "waiting deps" });
let thisClassName = "db"; //mysql is somehow a reserved key, and does not get added to globals..
let self = ()=>global.get(thisClassName);
let getThisClass = () => global.get(thisClassName);

/** POST-GEN PROCEDURE
 *  used to generate stuff that depends on other stuff, or it's a function of it.
 *  ex: "mySillyProp": afterDef( def => { /* parse def for values * / def.mySillyProp = generatedValue; }, "mySillyProp");
 *      - postGen: array of cb, each will be called after def has been instantiated. (WON'T BE IN GLOBAL THO! use from args)
 *      - postGenInfo: array of cbNames. Optional, but encouraged as they make it easier to find errors.
 *      - afterDef(def): entrypoint func, used to add stuff to the post-generation procedure.
 *      - postGenGroupedMsgList: used to group all warn and error messages into one
 *      - print_postGen_errors(): used to group all warn and error messages into one */
let postGen = []; let postGenInfo=[]; let postGenGroupedMsgList=[]; let afterDef = (func, optionalFName) => {
    postGen.push(func ?? (()=>{ postGenGroupedMsgList.push({ cat: "nullCb", idx: postGen.length})})() ?? null);
    postGenInfo.push(optionalFName ?? (()=>{ postGenGroupedMsgList.push({ cat: "noFName", idx: postGen.length-1})})() ?? " undefName");
    return "toBeSet_afterward";
};
function print_postGen_errors(){
    let map = {}; postGenGroupedMsgList.forEach(pair => map[pair.cat] = map[pair.cat] ? pair : [pair]);
    if(postGenGroupedMsgList?.length)
        node.warn({
            txt: `[WARNING] in afterDef() of global.${thisClassName} `
                + (!map["nullCb"] ? ''   : `\n ${map["nullCb"]?.length} nullish callbacks found in afterDef, for ${map["nullCb"]?.map?.(pair => postGenInfo?.[pair?.idx])?.join(', ')}`)
                + (!map["noFName"] ? ''  : `\n ${ map["noFName"]?.length } of ${ postGen?.length } callbacks miss a displayName.This will make it harder to pinpoint errors.`),
            allErrs: map,
            nullCb: !map["nullCb"] ? undefined : { txt: `${map["nullCb"]?.length} nullish callbacks found in afterDef`, idxs: map["nullCb"]?.map?.(pair => pair.idx), fNames: map["nullCb"]?.map?.(pair => postGenInfo?.[pair?.idx])},
            noFName: !map["noFName"] ? undefined : { txt: `${map["noFName"]?.length} of ${postGen?.length} callbacks miss a displayName. This will make it harder to pinpoint errors.`, idxs: map["noFName"]?.map?.(pair => pair.idx), fNames: map["noFName"]?.map?.(pair => postGenInfo?.[pair?.idx]) }
        });
}

const sqlString = getSqlString();

callWhenDef("utils",()=>{
    const utils = global.get("utils");
    let def = {
        "connCfg":{
            username:"nodered_client",
            database:"oven",
            host:"localhost",
            port: 3306,
            password:"Luca140583",
        },
        /** @summary chiamato sui messaggi in uscita dal nodo db per non portarsi dietro i dati della connessione.
         * @param {Object | Array} exceptMap lista di chiavi da mantenere. Accetta {}, [] e nullish. */
        "detachConnCfg": function thisFunc(msg, exceptMap = {}){
            if(!msg) throw new Error("msg cannot be null. msg:"+msg);
            //array to hashMap
            if(Array.isArray(exceptMap)){
                let tmp = exceptMap;
                exceptMap = {};
                tmp.forEach(key => exceptMap[key] = true);
            }
            for(var [key, _] of Object.entries(this.connCfg))
                exceptMap?.[key] || delete msg[key];
            return msg;
        },
        /** @summary 
            *  @desc 
            * @param {string} query valid mysql string
            * @param {Object} msg the source msg object to use
            * @param {function} onErr the cb fucntion that will be called if the query fails
            */
        "attachQuery": function thisFunc(queryStr, msg = new Object(), onSqlErr = null) {
            if (typeof (queryStr) != "string")
                throw new Error("Argument Type Mismatch: query string vs " + typeof (queryStr));
            //copia tutto da connCfg (senza sovrascrivere)
            let connCfg = global.get(thisClassName).connCfg;
            for(var [key, val] of Object.entries(connCfg))
                if(msg[key] === undefined)
                    msg[key] = connCfg[key];            
            //allega query
            msg.sql = queryStr;
            msg.onSqlErr = onSqlErr;
            return msg;
        },
        /** easier to use sqlString.format() to build the whole query */
        "sanitize": function sanitize(str){
            global.get(thisClassName).sqlString.escape(str);
        },
        /** brute copy from https://www.npmjs.com/package/sqlstring?activeTab=readme
         *  basic sanitizing and formatting for sql strings */
        "sqlString": sqlString,
        /** multi-try sql query */
        "polledQuery":{
            "pollCtxKey":"dbPollGet",
            /** collection of keys of pollCtx's common vars */
            "pollCtxPropNameList": [
                "attempts", "startTime", "sqlErrorList", "isPollComplete", //core
                "maxAttempts","delay","toutTolerance","toutDelay","toutTime","readableTimes",//cfg
                "query", "isSuccess", "lastTryTime", "isDbgWrapped", "endTime", "sqlErrorList_grouped" //others //(explicit or pollStep-defined)
            ],
            /** on startup verifies that all pollCtx props have been manually registered in pollCtxPropNameList.
             *  Then delete itself.
             *      can't use afterDef() to gen them cuz init() needs access to global.db
             *      can't gen them with callWhenDef() cuz it breaks the contract: when global.db is set it must be COMPLETE, ence it must hold this prop as well.  */
            "selfPoppingTest_pollCtxPropNameList":callWhenDef("db",()=>{
                const dbPoll = global.get("db").polledQuery;
                var dummyMsg = {}; var dummyNode = node;
                dbPoll.initPollCtx_core(dummyNode, dummyMsg);
                dbPoll.initPollCtx_cfg(dummyNode, dummyMsg,{
                    maxAttempts:0,
                    delay:0
                });
                //get all props defined by the initer funcs + the ones defined inside performPollStep()
                let missing = Object.entries(dummyMsg[dbPoll.pollCtxKey]).map(pair => pair[0]).concat([
                    "endTime", "sqlErrorList_grouped"
                ]).filter(defKey => !dbPoll.pollCtxPropNameList.includes(defKey));
                if(missing?.length)
                    node.warn({ txt:"[WARNING] some props are missing from pollCtxPropNameList", missing:missing})
                delete dbPoll.selfPoppingTest_pollCtxPropNameList;
            }),
            /** init basic context properties. Same across all nodes  */
            "initPollCtx_core": function thisFunc(node, msg){
                if (thisFunc.length != arguments.length || [...arguments].some(arg => !arg))
                    throw new Error(`${thisFunc.length} non null args required. got ${arguments.length}: ${[...arguments].map(item => "" + item)}`);

                const isDbg = (lvl) => global.get(thisClassName).polledQuery.dbgSettings.isDbgSetFor(node, msg, lvl);
                //clear ctx coming from other poll nodes
                var pollCtx = msg[global.get(thisClassName).polledQuery?.pollCtxKey];
                if(msg[global.get(thisClassName).polledQuery?.pollCtxKey]?.isPollComplete){
                    isDbg("debug") && node.warn({txt:"deleting lingering pollCtx..", ctx:pollCtx,full:msg,node:node});
                    delete msg[global.get(thisClassName).polledQuery?.pollCtxKey];
                }
                var pollCtx = msg[global.get(thisClassName).polledQuery?.pollCtxKey] ?? (msg[global.get(thisClassName).polledQuery?.pollCtxKey] = {});
                //dbPollGet common fields
                pollCtx.attempts ?? (pollCtx.attempts = 0);
                pollCtx.startTime ?? (pollCtx.startTime = Date.now());
                pollCtx.sqlErrorList ?? (pollCtx.sqlErrorList = []);
                pollCtx.isPollComplete ?? (pollCtx.isPollComplete = false);
                pollCtx.lastTryTime ?? (pollCtx.lastTryTime = null);
                pollCtx.isDbgWrapped ?? (pollCtx.lastTryTime = false);
            },
            /** common cfg init pattern. Nodes may explicitly init pollCtx
             *  specific nodes may define additional ctx parameters, as required by their isSuccess() or inner workings
             *  missing props from here:
             *  - isSuccess() : true|false      controls when the polling stops
             *  - query : string                sql query string
             *  - custom stuff */
            "initPollCtx_cfg": function thisFunc(node, msg, { maxAttempts, delay, toutTolerance=null, toutDelay=null, toutTime=null}){
                if (thisFunc.length != arguments.length || [...arguments].some(utils.isNotSet))
                    throw new Error(`${thisFunc.length} non null args required. got ${arguments.length}: ${[...arguments].map(item => "" + item)}`);
                if([maxAttempts, delay].some(utils.isNotSet))
                    node.warn({ txt:"[global db]: initPollCtx_cfg(): parameter with undefined value",fName:thisFunc.name, args:arguments, calledBy:this});
                //node-specific cfg
                const pollCtx = msg[global.get(thisClassName).polledQuery?.pollCtxKey] ?? (() => { throw new Error(`missing pollCtx (msg.${global.get(thisClassName).polledQuery?.pollCtxKey})`) })(); 
                pollCtx.maxAttempts ?? (pollCtx.maxAttempts = maxAttempts);
                pollCtx.delay ?? (pollCtx.delay = delay);
                pollCtx.toutTolerance ?? (pollCtx.toutTolerance = toutTolerance ?? pollCtx.maxAttempts * 150);
                pollCtx.toutDelay ?? (pollCtx.toutDelay = Math.max(toutDelay, pollCtx.maxAttempts * pollCtx.delay + pollCtx.toutTolerance));
                pollCtx.toutTime ?? (pollCtx.toutTime = toutTime ?? pollCtx.startTime + pollCtx.toutDelay);
                //human-readable copies
                pollCtx.readableTimes ?? (pollCtx.readableTimes = pollCtx.readableTimes ?? (() => {
                    let root = {};
                    ["delay", "toutDelay", "toutTolerance"].forEach(timeKey => { root[timeKey] = global.get("utils").msToScaledString(pollCtx[timeKey]); });
                    ["toutTime", "lastTryTime"].forEach(timeKey => { root[timeKey] = global.get("utils").msToScaledString((pollCtx[timeKey] ?? pollCtx.startTime) - pollCtx.startTime) + " since start"; });
                    return root;
                })());
            },
            /** call this if you explicitly define pollCtx members, to prehemptively catch errors
             * verify all PollCtx props are defined and valid */
            "validatePollCtxInit": function thisFunc(node, msg){
                const pollCtx = msg[global.get(thisClassName).polledQuery?.pollCtxKey];
                //check all defined //except a couple ones
                const excluded = ["lastTryTime", "isDbgWrapped", "endTime", "sqlErrorList_grouped"]
                var isValid = this.pollCtxPropNameList.every(key => excluded.includes(key) || utils.isSet(pollCtx[key]));
                if(!isValid)
                    try{
                        node.error({
                            "txt": "failed pollCtx validation", "reason": "one or more items are nullish",
                            "nullish": this.pollCtxPropNameList.filter(propName => utils.isNotSet(pollCtx[propName]) && !excluded.includes(propName)),
                            "pollCtx": pollCtx, "required": this.pollCtxPropNameList, "full": msg
                        });
                    } catch (ex) { //paranoic error printing
                        node.error({ txt: "failed pollCtx validation (and error generation)", reason: "one or more items are nullish",full:msg, innerEx:ex });
                    };
                return isValid;
            },
            "setNodeDisplayStatus": function thisFunc(srcNode, msg, statusKey) {
                try{ //should never break a flow, as it's meant as a simple dbg aid
                    if (thisFunc.length != arguments.length || [...arguments].some(arg => !arg))
                        throw new Error(`${thisFunc.length} non null args required. got ${arguments.length}: ${[...arguments].map(item => "" + item)}`);
                    const utils = global.get("utils");
                    let pollCtx = msg[global.get(thisClassName).polledQuery.pollCtxKey];
                    let getCfgTriplet = () => `${pollCtx.maxAttempts}t /${utils.msToScaledString(pollCtx.delay)} /${utils.msToScaledString(pollCtx.toutDelay)}`
                    switch (statusKey) {
                        case "inited":          { srcNode.status({ fill: "blue",   shape: "dot", text: `init with ${getCfgTriplet()}` }); break; }
                        case "willRetry":       { srcNode.status({ fill: "yellow", shape: "dot", text: `will #${pollCtx.attempts} of ${getCfgTriplet()}` }); break; }
                        case "awaitingResponse":{ srcNode.status({ fill: "yellow", shape:"ring", text: `sent #${pollCtx.attempts} of ${getCfgTriplet()}` }); break; }
                        case "success":         { srcNode.status({ fill: "green",  shape: "dot", text: `ok at #${pollCtx.attempts} /${utils.msToScaledString(pollCtx.endTime - pollCtx.startTime)} of ${getCfgTriplet()}` }); break; }
                        case "finalFail":       { srcNode.status({ fill: "red",    shape: "dot", text: `aborted at #${pollCtx.attempts} /${utils.msToScaledString(pollCtx.endTime - pollCtx.startTime)} of ${getCfgTriplet()}` }); break; }
                        default: { node.error({ txt: `[global.${thisClassName}] ${thisFunc.name}(): unknown node display status ` + statusKey, src: srcNode, full: msg, statKey: statusKey }, msg) }; break;
                    }
                }catch(e){ node.error(null,{txt:"[WARN] cannot display pollCtx node status", srcNode:srcNode, msg:msg})}
            },
            /** sends a db query and/or success/fail responses on different channels */
            "performPollStep": function thisFunc(srcNode, msg){
                if(thisFunc.length != arguments.length || [...arguments].some(arg => !arg))
                    throw new Error(`${thisFunc.length} non null args required. got ${arguments.length}: ${[...arguments].map(item => ""+item)}`);
                const pollCtx = msg[global.get(thisClassName).polledQuery?.pollCtxKey] ?? (()=>{throw new Error("missing pollCtx")})();
                const utils = global.get("utils");                
                const dbPoll = global.get(thisClassName).polledQuery;

                const isDbg = (lvl) => dbPoll.dbgSettings.isDbgSetFor(srcNode, msg, lvl);
                const sendQuery = () => {
                    isDbg("debug") && srcNode.warn({txt:"sending query",q:pollCtx.query,ctx:pollCtx,full:msg});
                    pollCtx.attempts++;
                    pollCtx.lastTryTime = Date.now();
                    pollCtx.readableTimes.lastTryTime = utils.msToScaledString(pollCtx.lastTryTime - pollCtx.startTime) + " since start";
                    global.get(thisClassName).attachQuery(pollCtx.query, msg)
                    srcNode.send([null, null, global.get(thisClassName).attachQuery(pollCtx.query, msg) ?? node.error("!! nothing done"), null])
                }
                isDbg("debug") && srcNode.warn({ txt: "dbPoll pre-step", ctx: pollCtx, full: msg, srcNode: srcNode });

                //wrap isSuccess()   
                    //aid in error pinpointing
                if(utils.isSet(pollCtx.isSuccess) && !pollCtx.isDbgWrapped){
                    utils.wrapFunctions(pollCtx, "[pollCtx of node " + srcNode.id + "]" );
                    pollCtx.isDbgWrapped = true;
                }
                //if is first attempt
                if(pollCtx.attempts == 0){
                    sendQuery(); //attempts++
                    dbPoll.setNodeDisplayStatus(srcNode, msg, "inited");
                    return null;
                }
                else{
                    var outParam_errTxt = {};
                    //on success -> return
                    if (pollCtx.isSuccess(srcNode, msg, outParam_errTxt)){
                        //lock function value
                        pollCtx.isSuccess_originalFunc = pollCtx.isSuccess;
                        pollCtx.isSuccess = function(){return true}; 
                        //set info "pollCompleted"
                        pollCtx.isPollComplete = true;
                        pollCtx.endTime = Date.now();
                        dbPoll.setNodeDisplayStatus(srcNode, msg, "success");
                        return [msg, null, null, null];
                    }
                    //on fail
                    else{
                        node.warn({txt:"",ctx:pollCtx, list:pollCtx.sqlErrorList});
                        //move error result to pollCtx
                        pollCtx.sqlErrorList.push({
                            "txt": outParam_errTxt.value,
                            "error": msg.error,
                            "_error": msg._error,
                            "payload": msg.payload
                        });
                        delete msg.error;
                        delete msg._error;
                        delete msg.payload;
                        //if not out of attempts: retry
                        if (pollCtx.attempts < pollCtx.maxAttempts && Date.now() < pollCtx.toutTime){
                            isDbg("verbose") && srcNode.warn("will send next query in " + utils.msToScaledString(pollCtx.delay));
                            dbPoll.setNodeDisplayStatus(srcNode, msg, "willRetry");
                            setTimeout(()=>{
                                isDbg("verbose") && srcNode.warn({ txt: "sent next query after " + utils.msToScaledString(pollCtx.delay), ctx: msg[global.get(thisClassName).polledQuery.pollCtxKey], full: msg});
                                sendQuery();
                                dbPoll.setNodeDisplayStatus(srcNode, msg, "awaitingResponse");
                            }, pollCtx.delay);
                        }
                        else{
                            //lock function value
                            pollCtx.isSuccess_originalFunc = pollCtx.isSuccess;
                            pollCtx.isSuccess = function(){return false};
                            //set info "pollCompleted"
                            pollCtx.isPollComplete = true;
                            pollCtx.endTime = Date.now();
                            pollCtx.readableTimes.endTime = global.get("utils").msToScaledString((pollCtx.endTime ?? pollCtx.startTime) - pollCtx.startTime) + " since start";
                            //set info "sqlErrorList_grouped"
                            /**
                             * idxMap will look like { 
                             *  <errTxt1>: <idx in sqlErrorList_grouped>,
                             *  <txt2>: <idx2>, 
                             *  ..
                             * }
                             * meanwhile pollCtx.sqlErrorList_grouped will hold one row for each mapped text. */
                            pollCtx.sqlErrorList_grouped = [];
                            const idxMap = {}; pollCtx.sqlErrorList.forEach(errObj => idxMap[errObj.txt] ?? (idxMap[errObj.txt] = pollCtx.sqlErrorList_grouped.push({
                                txt: errObj.txt,
                                //error
                                //_error
                                count:0, //will be incremented later on
                            })-1));
                            //node.warn({txt:"after idxMap init",idxMap:idxMap, errList:pollCtx.sqlErrorList, grouped:pollCtx.sqlErrorList_grouped, ctx:pollCtx})


                            for (var errObj of pollCtx.sqlErrorList){
                                let groupedItem = pollCtx.sqlErrorList_grouped[idxMap[errObj.txt]];
                                groupedItem.count++;
                                //merge props of 1st err of that kind //assume rest are equal
                                Object.entries(errObj).forEach(([key, val])=>{
                                    groupedItem[key] ?? (groupedItem[key] = val);
                                })
                            }
                            isDbg("debug") && node.warn({ txt:"sqlErrorList_grouped set to",idxMap:idxMap, errList:pollCtx.sqlErrorList, grouped:pollCtx.sqlErrorList_grouped, ctx:pollCtx})
                            srcNode.warn({ txt: "final fail", gErr: pollCtx.sqlErrorList_grouped, ctx: pollCtx, full: msg });
                            dbPoll.setNodeDisplayStatus(srcNode, msg, "finalFail");
                        }

