        pollCtx.delay       ??= 2 * 1000;
        pollCtx.toutTolerance       = pollCtx.maxAttempts * 150;
        pollCtx.toutDelay       ??= pollCtx.maxAttempts * pollCtx.delay + pollCtx.toutTolerance;
        pollCtx.toutTime        ??= pollCtx.startTime + pollCtx.toutDelay
        pollCtx.isSuccess ??= function => true | false

