/**
 * manual switch, controlled by inject nodes with msg.control:"<cmd>" and msg.payload:<cmd args>
 * the status displays the active route, meaning the output idx non-control msgs will be fwd to
 * */
context.set("onMsg", function (msg) {
    const ctxGet = (vName, defVal) => context.get(vName) ?? context.set(vName, defVal) ?? context.get(vName);
    const setCtx = (vName, val) => context.set(vName, val) ?? context.get(vName);
    const utils = global.get("utils") ?? node.error({ txt: "utils not yet def. Delayed msg.. (order won't be guaranteed!)", t: Date.now(), n: node, msg: msg }) ?? callWhenDef("utils", () => onMsg(msg));

    //optional route names to display (fill up tmp in (tmp=[...]).
    //Missing ones will use their idx)
    var tmp;
    const routeNames = ctxGet("routeNames", (tmp = []).concat(utils.range(tmp.length, node.outputCount)));
    context.get("routeIdx") ?? context.set("routeIdx", 0);
    //var currRouteIdx = ctxGet("routeIdx", 0); 

    let setRouteIdx = (idx) => {
        if (idx < 0 || idx >= node.outputCount)
            node.warn({ txt: `[WARN] set to idx out of range (${msg.payload} in 0-${node.outputCount}). Msgs will go nowhere.`, full: msg, n: node, max: node.outputCount })
        context.set("routeIdx", idx);
        node.status({ fill: idx != wrappedIdx(idx) ? "red" : idx % 2 == 0 ? "green" : "yellow", shape: "dot", text: `${idx}${/^[0-9]*$/.test(routeNames[idx]) ? "" : ` : ${routeNames[idx]}`}` });
    }
    //let setToNext = () => setRouteIdx(currRouteIdx + 1 >= node.outputCount ? 0 : currRouteIdx + 1);
    function wrappedIdx(idx) { return global.get("utils").wrapIntAround(idx, 0, node.outputCount - 1); }

    if (utils.isSet(msg.control)) {
        switch (msg.control) {
            case "toggle":
            case "next": {
                setRouteIdx(wrappedIdx(context.get("routeIdx") + 1));
                return;
                //return setRouteIdx(currRouteIdx + 1 >= node.outputCount ? 0 : currRouteIdx + 1)
            }
            case "setSafe": { msg.payload = wrappedIdx(msg.payload); }
            case "set": {
                // if(msg.payload >= node.outputCount)
                //     node.warn({txt:`[WARN] set to idx out of range (${msg.payload} in 0-${node.outputCount}). Msgs will go nowhere.`,full:msg, n:node, max:node.outputCount})
                // return context.set("routeIdx", msg.payload);
                setRouteIdx(msg.payload);
                return;
            }
            default: {
                node.error({ txt: `unknown control value (${msg.control}). msg forwarded on curr channel` })
                sendOnRouteIdx(context.get("routeIdx"));
                return;
            }
        }
    } else {
        sendOnRouteIdx(context.get("routeIdx"));
        return;
    }

    function sendOnRouteIdx(idx) {
