                    sharedBits[bit].values[pair.macKey] = false;
                })
            }
        }
        //add iterators
            //for sharedBits
            //for each sharedBits[].values
        let valKeys;
        utils.generateIteratorFor(sharedBits);
        // @ts-ignore
        for(let item of sharedBits){
            valKeys = Object.keys(item.values).map(v=>v);
            item.values[Symbol.iterator] = utils.makeKeyIterator(valKeys);
        }
    }
    //node.warn({"sharedBits":sharedBits });
    // sharedBits.forEach( bitItem => bitItem.values.fill(true) );
    // @ts-ignore
    //for(let bitItem of sharedBits) for(let mk in bitItem.values) bitItem.values[mk] = true;



    //towersControl
    var def = {
        /** holds allowed keys as <string>[] //also used for validation purposes on towersControl definition
             * - {String[]} macKeys "machine keys". match the ones used in uibuilder's app.
             * - {String[]} devices devices that may be associated with each towersBitArray bit */
        "dictionary": dictionary,

        /** pairs devices with the corresponding bit index to use in the modbus  queries
            example: posOf[macKey][device]:number    //undefined macKey -> exception like 'undefined reading undefined' //undefined device -> exception like 'undefined reading <device>'
            exposed but not expected to be used.
            "posOf":{
                {macKey}:{  //all dictionary.macKeys are defined and objects
                    {device}:0 < number < 2^n | errorLoggingGetter() //bit index //all dictionary.devices are defined or errorLogging
                }
            },*/
        "posOf": posOf,

        /** 

          "sharedBits":{
                {bit}:{ //only the shared ones are defined
                    'devKey': <devKey> //associated device. //it presumes all machines sharing the bit use the same device
                    'output'
                }
           }*/
        "sharedBits": sharedBits,

        /** array of bits, represented by Numbers that are either 0 or 1
             * - values should be set through towersControl["FA22"].redLight = truthy|falsy
             * - has a lenght of NBITS */
        "towersBitArray": new Array(16).fill(0),

        /** @returns Bool
         * @throws TypeError if macKey or devKey are not in dictionary */
        "macHasDevice": function thisFun(macKey, devKey) {
            if (arguments.length != thisFun.length) throw new SyntaxError(`[towersControl:macHasDevice()] expected ${thisFun.length} arguments, got ${arguments.length}`);
            if (!posOf[macKey] || posOf[macKey][devKey] === undefined) throw new TypeError("[towersControl:macHasDevice()] uregistered macKey or devKey:" + macKey + ", " + devKey);

            return this.posOf[macKey] && typeof (this.posOf[macKey][devKey]) == "number"
            //return this[macKey] && this[macKey].propertyIsEnumerable(devKey)
        },
        /*  
        {macKey}:{  //all dictionary's macKeys are defined. 
            'hasDevice'(...): this.macHasDevice.bind(def, <macKey>, ...)    //alias with one less parameter //not enum
            {linkedDeviceKey}: truthy|falsy,     //only posOf[<macKey>]'s devKeys are defined. //dictionary's devKeys missing in posOf[<macKey>] are instead not-enumerable warning-loggers
            {linkedDeviceKey}: truthy|falsy,
            ..
        },
        ..
        */
        [Symbol.iterator]: utils.makeKeyIterator(dictionary.macKeys)
    }



    //complete towersControl
        //adds a setter and getter for EACH macKey and linkedDeviceKey, even if they are not registered in posOf
        //they will modify the corresponding towersBitArray's pos instead of themselves.
        //setters convert the value to either 0 or 1. //not true/false for easier math and conversions
        //setters&getters paired with unconfigured devices (posOf[][]===null) will be NOT ENUMERABLE and log a warning
        //adds a NOT ENUMERABLE hasDevice() function, which aliases macHasDevice(macKey,...)
    {
        const devIsRegistered = function (macKey, linkedDeviceKey) {
            return posOf[macKey][linkedDeviceKey] !== null && posOf[macKey][linkedDeviceKey] !== undefined
        }
        const devIsShared = function (macKey, linkedDeviceKey){
            return devIsRegistered(macKey,linkedDeviceKey) && sharedBits[posOf[macKey][linkedDeviceKey]]
        }
        for(let macKey of dictionary.macKeys) {
            def[macKey] = {};
            Object.defineProperty(def[macKey], "hasDevice", {
                value: def.macHasDevice.bind(def, macKey)
            });
            for(let linkedDeviceKey of dictionary.devices) {
                //node.warn("setting "+macKey+ " "+linkedDeviceKey)
                //if device is not configured add an error-logging getter/setter
                if(true || devIsRegistered) {
                    Object.defineProperty(def[macKey], linkedDeviceKey, {
                        //if bit is registered aka (typeof posof[][] == "number") <=> !== null && >=0
                        set(val) {
                            let idx = def.posOf[macKey][linkedDeviceKey];
                            //node.warn({txt:"setting "+macKey+" "+linkedDeviceKey+" at "+idx+":"+val, isDiff:def!==global.get("towersControl")});
                            if (devIsRegistered(macKey, linkedDeviceKey)){
                                //if bit/device is shared by multiple machines
                                if(def.sharedBits[idx]){
                                    def.sharedBits[idx].values[macKey] = (val ? true : false) //store the state for that machine
                                    def.towersBitArray[idx] = (def.sharedBits[idx].output() ? 1 : 0); //set using the output function defined for that device.
                                }
                                else
                                    def.towersBitArray[idx] = (val ? 1 : 0);
                            }
                            else
                                onUnconfigKeySet(macKey, linkedDeviceKey);
                        },
                        get() {
                            let idx = def.posOf[macKey][linkedDeviceKey];
                            if(idx !== null && idx !== undefined){
                                //if bit/device is shared by multiple machines
                                if(def.sharedBits[idx])
                                    return def.sharedBits[idx].values[macKey] ? 1 : 0;
                                else
                                    return def.towersBitArray[idx]
                            }else{
                                onUnconfigKeyGet(macKey, linkedDeviceKey);
                                return undefined;
                            }
                        },
                        enumerable: def.macHasDevice(macKey, linkedDeviceKey)
                    })
                }
                else {
                    Object.defineProperty(def[macKey], linkedDeviceKey, {
                        set(val) {
                            onUnconfigKeySet(macKey, linkedDeviceKey);
                        },
                        get() {
                            onUnconfigKeyGet(macKey, linkedDeviceKey);
                            return undefined;
                        },
                        enumerable: false
                    })
                }
            }
        }
    }

    //validate towersBitArray syntax

    /*
    //complete posOf
        //attach to each null key an error-logging function
        //--EDIT--allow for posOf calls on missing one
    for (let macKey in posOf) {
        for (let linkedDeviceKey in posOf[macKey]) {
            if (posOf[macKey][linkedDeviceKey] === null) {
                Object.defineProperty(posOf[macKey], linkedDeviceKey, {
                    set(val) {
                        onUnconfigKeySet(linkedDeviceKey, macKey)
                    },
                    get() {
                        onUnconfigKeyGet(linkedDeviceKey, macKey);
                        return null;
                    }
                });
            }
        }
    }
    */

    global.set("towersControl", def);
    global.set("towersBitArray", global.get("towersControl").towersBitArray)
    node.send(def);
    node.status({ fill: "green", text: Object.keys(def).join(' & ') });

