class toutItem {
    #startTime
    #endTime
    //remainingMs
    #cb
    #ref

    constructor(cb,delay){
        this.#startTime = Date.now();
        this.#endTime = this.startTime + delay;
        this.#cb = cb;
        this.#ref = setTimeout(cb,delay);
    }

    get remainingMs(){ return this.#startTime - this.#endTime }
    get startTime() { return this.#startTime }
    get endTime() { return this.#startTime }
    get cb(){ return this.#cb }
    get ref(){ return this.#ref }

    get timerLength(){return msToScaledString(this.startTime - this.endTime) }
    get remainingTime(){ return msToScaledString(this.endTime - Date.now()) }
}

