class HTMLSelectElement extends HTMLElement {
    // tag
    // attributes
    // innerHtml
    // html
    options

    // isHtmlUpdated
    // isLogOn
    //#isInnerHtmlUpdated
    _isInnerHtmlUpdated
    get isInnerHtmlUpdated(){ return this._isInnerHtmlUpdated }
    set isInnerHtmlUpdated(val){ this._isInnerHtmlUpdated=val; if(!val) this.isHtmlUpdated = false }

    constructor({ attributes, options, innerHtml, html }){
        const isLogOn = false;
        // if(typeof(arguments[0]) == "string")
        //     (html = arguments[0])       && (!isLogOn || node.warn("[HTMLSelectElement] setting html to arguments[0]:" + html))

        //align options and innerHtml to each other
        if(options && !innerHtml) // @ts-ignore
            var innerHtml = options.map(opt => opt.updateHtml()).join("\n\t") + "\n";
        else if(!options && innerHtml)
            var options = HTMLSelectElement.explodeOptions(innerHtml);
        isLogOn && node.warn({ txt: "aligned opt and inner ", opt: options, inner: innerHtml, willCallSuperWith:arguments })


        //super({ html:html ?? `<select ${ HTMLElement.toTagContent(toAttr(attributes)) }>${innerHtml}</select>` });
        typeof(arguments[0])=="string" ? super(arguments[0]) : super({tag:"select", attributes, innerHtml, html});
        isLogOn && node.warn({txt:"created super() of select", halfInited:this});
        this.options = options ?? HTMLSelectElement.explodeOptions(this.innerHtml)

        this.isInnerHtmlUpdated = true;
    }

    updateInnerHtml(forceUpdate){
        if(forceUpdate ?? this.isInnerHtmlUpdated){
            this.isInnerHtmlUpdated = true;
            return this.innerHtml = this.options.map(opt => opt.updateHtml()).join("\n\t")+"\n";
        }
    }

    updateHtml(forceUpdate){
        this.updateInnerHtml(forceUpdate);
        return super.updateHtml(forceUpdate);
    }

    setSelected(valueOrOpt){
        this.isInnerHtmlUpdated = false;
        let toSelect = (typeof(valueOrOpt)=="object" && valueOrOpt.prototype instanceof HTMLOptionElement) ? valueOrOpt : this.options.find( opt => opt.value == valueOrOpt)
        this.options.forEach( opt => (opt === toSelect) ? opt.setAttribute("selected") : opt.removeAttribute("selected"))
        return this;
    }

    //tested on select html or options-only html //shuould work even on a whole page's html
    static explodeOptions(html, isLogOn=false){
        if(typeof (html) !== "string") return node.error({ err: "[HTMLSelectElement explodeOptions] argument is invalid", html: html, args: arguments, e: new TypeError("[HTMLElement explodeHtml] argument is invalid") })
        return [...(html.matchAll(/<\s*option(?:(?<!<\/option).)+\s*>/sg) || [])].map(regExpRes => { isLogOn && node.warn({txt:"creating opt",from:regExpRes[0], full:regExpRes }); return new HTMLOptionElement({ html:regExpRes[0] })  })  //regExpRes is an iterator over [0:fullOptHtml, index:firstCharPos, input:originalString, groups:undefined]
    }

    // implemented in HTMLOptionElement
    // getOptionValue(opt){
    //     opt.getAttribute("value") ?? opt.innerHtml
    // }
    hasOption(value) { return this.getOption() !== undefined }
    getOption(value) {  // @ts-ignore
        const isLogOn = (false && this.isLogOn) || this.isLogOn > 1;
        isLogOn && node.warn({"options is":this.options, lookingFor:value, found:this.options.find(opt => opt.value == value) ?? undefined})
        return this.options.find(opt => opt.value == value) ?? undefined;
    }
    // newOption(name, value){
    //     this.isInnerHtmlUpdated = false;
    //     var newOpt = { "name":name, "value":""+value ?? "", "isValueMissing": value===undefined ? true : false };
    //     if(this.hasOption(name))
    //         newOpt = this.setOption(name, value);
    //     else
    //         this.options.push(newOpt)
    //     return newOpt;
    // }
    // setOption(valueOrOpt, value){
    //     this.isInnerHtmlUpdated = false;
    //     let opt = typeof(valueOrOpt == "object") ? valueOrOpt : this.getOption(valueOrOpt)
    //     return (opt===undefined) ? this.newOption(valueOrOpt, value) : Object.assign(opt, { "value":""+value ?? "", "isValueMissing": value===undefined ? true : false } )
    // }
    removeOption(valueOrOpt){
        this.isInnerHtmlUpdated = false;
        let opt = typeof (valueOrOpt == "object") ? valueOrOpt : this.getOption(valueOrOpt)
        let removeIdx = (i,arr) => {arr[i] = arr[arr.length-1]; arr.pop(); return arr}
        if(opt !== undefined)
            removeIdx(this.options.findIndex(item => item.value == opt.value), this.options)
        return opt !== undefined //was there something to remove ? true : false
    }
}

