function getView(viewKey){
    let config=global.get("config");
    var vk = viewKey;
    var loopGuardIdx = 99;
    do{
        if(!config.views[vk])
            throw new TypeError("unknown viewKey  " + vk);
        if(typeof(config.views[vk]) == "string"){
            vk = config.views[vk];
        }
        else
            return config.views[viewKey];
    }while(loopGuardIdx--)

    throw new Error("[getView()] retrieving view "+viewKey+" resulted in an infinite loop.");
}

function setViewForIp(viewKey, ip){
    let config = global.get("config");   

    //remove ip from all views.exactIpList (there should only be one)
    for(let vk in config.views){
        let view=config.views[vk];
        for(let i in view.exactIpList)
            if(view.exactIpList[i] == ip){
                view.exactIpList[i] = view.exactIpList[view.exactIpList.length-1];
                view.exactIpList.length--;
                node.warn({"removedFrom":view,"ip":ip})
            }
    }

    //add ip to selected view
    let view = getView(viewKey);
    if(view.exactIpList)
        view.exactIpList.push(ip);

}

function getViewForIp(ip) {
    let config = global.get("config");
    for (let vk in config.views) {
        let view = config.views[vk];
        if (view.exactIpList && view.exactIpList.includes(ip))
            return vk;
    }
    return "default";
}

