                "queued": "<center><span ><i class='fa fa-clock-o' style=\"margin-right:5px\"></i>  in attesa  </span></center>",
                    //button //onClick: reset to clearing and enqueue empty plc action
                "loaded":"<center><span ><i class='fa fa-check' style=\"color:green;\"></i>    </span></center>",
                    //state //onClick: reset to clearing and enqueue empty plc action
                "clearing": "<center><span ><i class='fa fa-check' style=\"color:gray;\"></i>    </span></center>",
                    //not used
                "fail": "<center><span ><i class='fa fa-times' style=\"color:red;\"></i></span></center>",
                    //something went wrong in a function
                "tableError": "<span style=\"color: red\"> tableError </span>"
            },

            getStateString(state) {
                var stateString;
                switch (state) {
                    case 0: { stateString = "clickToSend"; break; }
                    case 1: { stateString = "queued"; break; }
                    case 2: { stateString = "loaded"; break; }
                    case 3: { stateString = "clearing"; break; }
                    case -1: { stateString = "fail"; break; }
                    default: { node.error("unknown button state " + state + " ") }
                    case false: { stateString = "tableError"; break }
                }
                //node.warn({stateStringSetTo:stateString});
                return stateString;
            },
            getStateInt(stateString) {
                var state;
                switch (stateString) {
                    case "clickToSend": { state = 0; break; }
                    case "queued": { state = 1; break; }
