    //a gOod example -> aGOodExample
function toCamelCase(str) {
    return str.replace(/^(.)/, (m, chr) => chr.toLowerCase()).replace(/[^a-zA-Z0-9]+(.)/g, (m, chr) => chr.toUpperCase()).replace(/\s+/g, '');
}

function newDictEntry(item) {
    let nId = toCamelCase(item.name.trim());
    return { nameId: nId, id: item.id, currentName: item.name, get currentNameId() { return this.getter(this) }, currentNameIdBkp: "", getter: commonGetter }
}
//!! misses the trailing }
function newDictEntry_asString(item) {
    let nId = toCamelCase(item.name.trim());
    return "{ nameId:\"" + nId + "\", id:\"" + item.id + "\" , currentName:\"" + item.name + "\", get currentNameId() { return this.getter(this) }, currentNameIdBkp: \"\", getter: commonGetter ";
}

    //extract tabs and groups to sorted arrays
function extractTabsAndGroups(flowJson){
    let json = flowJson;
    var tabs = [];
    var groups = [];
    var dict = "";

    for (let item of json) {
        if (item.type == "ui_tab")
            tabs.push(item);
        else if (item.type == "ui_group")
            groups.push(item);
    }

    tabs.sort((a, b) => {
        return (!a.order || a.order < b.order) ? -1 : 1
    });
    groups.sort((a, b) => {
        if (a.tab == b.tab)
            return (!a.order || a.order < b.order) ? -1 : 1;
        else
            return (!a.tab < b.tab) ? -1 : 1;
    });

    return {tabs:tabs,groups:groups};
}


function generateDictionaryFromCurrentNames(flowJson) {
        //extract tabs and groups to sorted arrays
    const ui=extractTabsAndGroups(flowJson);
    var tabs=ui.tabs;
    var groups=ui.groups;
    var dict=new Array();

    //generate dictionary
    var tmpTabEntry;
    for (let tab of tabs) {
        tmpTabEntry = newDictEntry(tab);
        tmpTabEntry.groups = new Array();
        for (let group of groups)
            if(group.tab == tab.id)
                tmpTabEntry.groups.push(newDictEntry(group));
        dict.push(tmpTabEntry);
    }

    return dict;

}

function generateDictionaryFromCurrentNames_asString(flowJson) {
        //extract tabs and groups to sorted arrays
    const ui = extractTabsAndGroups(flowJson);
    var tabs = ui.tabs;
    var groups = ui.groups;
    var dict = "";

    //generate dictionary
    var tmpTabEntry;
    var tmpGroupList;
    dict+="[\n";
    for (let tab of tabs) {
        tmpTabEntry = "\t"+newDictEntry_asString(tab);
            //create group indented list
        tmpGroupList = "";
        for (let group of groups)
            if (group.tab == tab.id)
                tmpGroupList+="\t\t"+newDictEntry_asString(group)+"},\n";
            //put all togheter
        dict += tmpTabEntry + ", groups:[" + (tmpGroupList ? "\n"+tmpGroupList: "")+ "\t]},\n";
    }
    //dict = dict.replace(/..$/,'\n]'); //trailing ,\n -> \n]
    dict+="]";

    //copy-paste this, editing whatever you need to
    return dict;
}

